        product {
          groupedId: grouped_id
          deliveryMethods: available_shipping_carriers {
            deliveryCode: carrier_code
            __typename
          }
          sku
          availability
          brandName
          category: categoryNamePath
          name
          stock
          stockFromDc
          uid
          url
          productId: id
          image: thumbnail {
            url
            __typename
          }
          adjustQuantity {
            max
            min
            step
            __typename
          }
          backorderDeliveryDays: backorder_delivery_days
          priceRange: price_range {
            minimumPrice: minimum_price {
              finalPrice: final_price {
                value
                __typename
              }
              regularPrice: regular_price {
                value
                __typename
              }
              __typename
            }
            __typename
          }
          alsoBought: also_bought {
            productId: id
            sku
            name
            brandName
            category: categoryNamePath
            url
            unitPrice {
              unit
              __typename
            }
            image: thumbnail {
              url
              __typename
            }
            priceRange: price_range {
              minimumPrice: minimum_price {
                finalPrice: final_price {
                  value
                  __typename
                }
                regularPrice: regular_price {
                  value
                  __typename
