            catalogLabelActiveUntil: label_product_date_to
            __typename
          }
          maxDisplayedQty: max_displayed_qty
          __typename
        }
        unitPrice {
          unit
          value
          __typename
        }
        conversionInfo {
          conversionUnit: conversion_unit
          salesUnitsChange: sales_units_change
          conversionValue: value
          packSize: conversion_value
          __typename
        }
        __typename
      }
      availablePaymentMethods: available_payment_methods {
        amount {
          value
          currency
          __typename
        }
